mapper.select:
select * from xm_budget_record res

    ${@com.mdp.Util@trimWhere(ew.customSqlSegment)}


         and res.biz_month >= #{ext.startBizMonth} 
         and res.biz_month <= #{ext.endBizMonth} 


${@com.mdp.Util@trimOrderBy(ew.customSqlSegment,'')}

${@com.mdp.Util@trimGroupBy(ew.customSqlSegment,'')}

select ${groupBy},max(res.subject_name) as subject_name,max(res.username) as username,sum(res.budget_at) as budget_at from  xm_budget_record res

         and res.branch_id=#{branchId} 

         and res.project_id=#{projectId} 

         and res.subject_id =#{subjectId} 
         and res.cost_type =#{costType} 
         and res.biz_month >= #{startBizMonth} 
         and res.biz_month <= #{endBizMonth} 
     and exists ( select 1 from xm_collect_link l where l.collect_id=#{collectId} and l.project_id=res.project_id )

group by ${groupBy}

SELECT sum( ifnull(res.budget_at,0) ) AS budget_at, p.plan_nouser_at, p.plan_iuser_at, p.plan_ouser_at, p.locked, p.plan_workload, p.total_receivables, p.budget_margin_rate, p.contract_amt, p.plan_iuser_price, p.plan_ouser_price, p.plan_ouser_cnt, p.plan_iuser_cnt, p.plan_working_hours FROM xm_project p LEFT JOIN xm_budget_record res ON p.id = res.project_id
p.id = #{projectId} and res.id not in
#{item}
